'    l_req_privs t_sess_privs;',
'    l_priv varchar2(50);',
'    l_dummy pls_integer;',
'    l_priv_error  boolean := false;',
'begin',
'    l_req_privs(''CREATE SESSION'')       := 1;            ',
'    l_req_privs(''CREATE TABLE'')         := 1;',
'    l_req_privs(''CREATE VIEW'')          := 1;',
'    l_req_privs(''CREATE SEQUENCE'')      := 1;',
