'    $else',
'',
'      if admin_security_check then',
'        delete',
'          from logger_logs',
'         where logger_level >= p_purge_min_level',
'           and time_stamp < systimestamp - NUMTODSINTERVAL(l_purge_after_days, ''day'')',
'           and logger_level > g_permanent;',
'      end if;',
'    $end',
'    commit;',
'  end purge;',
